import './QueueDriver.css'
import { useState } from 'react';
export default function QueueDriver(props) {
    const [ queueDriverAppear, setQueueDriverAppear ] = useState(1);
    const [ latitudeStart, setLatitudeStart ] = useState(0);
    const [ longtitudeStart,setLongtitudeStart] = useState(0);
    const [ latitudeDestination, setLatitudeDestination ] = useState(0);
    const [longtitudeDestination,setLongtitudeDestination] = useState(0);
    let timeoutId1=0;
    
    
    function showQueue(data){
        if( !!! document.getElementById('queueList')){
            return;
        }
        let queueList = document.querySelector('#queueList');
        document.getElementById('queueList').innerHTML='';
        //let tmpQueueList = document.createElement('ul');
        data.forEach(val => {
            let myEl = document.createElement('span');
            myEl.innerText = `1 : ${val.driver_name} `;
            queueList.appendChild(myEl);
        })
        
    }
    
    
    timeoutId1 = setInterval(()=>{
            fetch("http://localhost:1235/queueDriver")
                .then(response=> response.json())
                .then(data => showQueue(data));
        },1500)
    
    function enQueue() {
        
        
        fetch("http://localhost:1235/queueDriver",{
                method: 'post',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    "id": 1,
                    "driver_name": "driver1"
                })
            })
            .then(response=> console.log(response))
            .catch(err => console.log(err));
            
         var timeoutId2=setInterval(()=>{
            fetch("http://localhost:1236/location")
                .then(response=> response.json())
                .then(data => {
                    if(data[0].status ==='true'){
                        updateStatus();
                        clearInterval(timeoutId1);
                        clearInterval(timeoutId2);
                        setLatitudeStart(data[0].latitudeStart);
                        setLongtitudeStart(data[0].longtitudeStart);
                        setLatitudeDestination( data[0].latitudeDestination);
                        setLongtitudeDestination(data[0].longtitudeDestination);
                        setQueueDriverAppear(null);
                        leaveQueue();
                }
            })
        }, 1500);
    }

    function leaveQueue() {
        fetch("http://localhost:1235/queueDriver/1",{
                method: 'delete',
                
            })
            .then(response=> console.log(response))
            .catch(err => console.log(err));
    }

    function updateStatus(){
        fetch("http://localhost:1234/response")
        .then(response=> response.json())
        .then(data=>{
            console.log(data[0]);
            if(data[0].status ==="false"){
                fetch("http://localhost:1234/response/1",{
                    method: 'put',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        "id": 1,
                        "status": "true",
                    })
                })
                .catch(err => console.log(err));
            }
        })
    }

    
    if(!!!queueDriverAppear){
        
        props.handleForUpdate(latitudeStart, longtitudeStart , latitudeDestination ,longtitudeDestination ,null )
    }   
    

    return(
        <div className="queue-container">
            <h4>ตารางคิว</h4>
            <div className="queue-list" id="queueList"></div>
            <div className="button-queue">
                <button className="button-enQueue" onClick={enQueue}> เข้าคิว </button>
                <button className="button-leaveQueue" onClick={leaveQueue}> ออกคิว </button>
            </div>
        </div>
    )
}
