import React from "react";
import './App.css'
import {
  InfoWindow,
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from "react-google-maps";

class App extends React.Component {
    onMarkerDragEnd = (event)=>{
      let newLat = event.latLng.lat();
      console.log('newLat',newLat);
    }

    state = {
      address: '',
      city: '',
      area: '',
      state: '',
      zoom: 15,
      height: 400,
      mapPosition: {
          lat: 0,
          lng: 0,
      },
      markerPosition: {
          lat: 0,
          lng: 0,
      }
    }


  render(){
    const MapWithAMarker = withScriptjs(withGoogleMap(props =>
      <GoogleMap
        defaultZoom={8}
        defaultCenter={{ lat: -34.397, lng: 150.644 }}
      >
        <Marker 
          draggable={true}
          onDragEnd={this.onMarkerDragEnd}
          position={{ lat: -34.397, lng: 150.644 }}


          >
          <InfoWindow>
            <div> wowza </div>
          </InfoWindow>
        </Marker>
      </GoogleMap>
    ));
    return(
      <MapWithAMarker
        googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyDmK5n48lyPTTnG3RgONpyDW3IpaygueZ4&v=3.exp&libraries=geometry,drawing,places"
        loadingElement={<div style={{ height: `100%` }} />}
        containerElement={<div style={{ height: `400px` }} />}
        mapElement={<div style={{ height: `100%` }} />}
      />
    );
  }
}

export default App;
